
CREATE TABLE Match_Result(
	id INTEGER identity(1,1) PRIMARY KEY,
	Date     datetime NOT NULL ,
	HomeTeam VARCHAR(24) NOT NULL,
	Result   VARCHAR(4) NOT NULL,
	AwayTeam VARCHAR(24) NOT NULL,
	HomeGoal INTEGER  NOT NULL,
	AwayGoal INTEGER  NOT NULL,

	foreign key (HomeTeam) references Point_Table(Team),
	foreign key (AwayTeam) references Point_Table(Team)
);

CREATE TABLE Point_Table(
	Pos  INTEGER  NOT NULL  ,
	Team VARCHAR(24) NOT NULL PRIMARY KEY,
	Pld  INTEGER  NOT NULL,
	Win  INTEGER  NOT NULL,
	Draw INTEGER  NOT NULL,
	Lose INTEGER  NOT NULL,
	GF   INTEGER  NOT NULL,
	GA   INTEGER  NOT NULL,
	GD   INTEGER  NOT NULL,
	Pts  INTEGER  NOT NULL,
);

CREATE TABLE Player_Stat(
	Team          VARCHAR(24) NOT NULL ,
	JerseyNo      INTEGER  NOT NULL ,
	Player        VARCHAR(25) NOT NULL,
	Position      VARCHAR(27) NOT NULL,
	Apearances    INTEGER  NOT NULL,
	Substitutions INTEGER  NOT NULL,
	Goals         INTEGER  NOT NULL,
	Penalties     INTEGER  NOT NULL,
	YellowCards   INTEGER NOT NULL,
	RedCards      INTEGER NOT NULL,
	PRIMARY KEY(Player, Team),
	foreign key (Team) references Point_Table(Team)
);





